package demo;

public class staticprg {
	static int i=90;
	public void changeVariable()
	{
		int i=50;
		i++;
		System.out.println(i);
		
	}
	
	
	
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		staticprg s = new staticprg();
		s.changeVariable();

	}

}
